const current = msg.payload.current;
let state = "IDLE";
if (current < desired - 1) state = "HEATING";
else if (current > desired + 1) state = "COOLING";
msg.payload = { temperature: desired, unit: "C", current, state };
return msg;

const currentTemp = msg.payload.temperature;
const desiredTemp = flow.get("desired_temp") || 24;
msg.payload = {
    temperature: currentTemp,
    desired: desiredTemp,
    unit: 'C',
    state: currentTemp > desiredTemp ? "ON" : "OFF"
};
return msg;

// Constants
const power_kW = 1.5; // Power usage of the AC in kW

// Get previous state and timestamp
const prevState = flow.get("ac_last_state") || "OFF";
const lastChange = flow.get("ac_last_change") || Date.now();
const energy = flow.get("energy_consumed") || 0;

const now = Date.now();
let newEnergy = energy;

if (prevState === "ON" && msg.payload.state === "OFF") {
    const durationHrs = (now - lastChange) / (1000 * 60 * 60);
    newEnergy += durationHrs * power_kW;
    flow.set("energy_consumed", newEnergy);
}

// Update flow context
flow.set("ac_last_state", msg.payload.state);
flow.set("ac_last_change", now);

msg.energy = newEnergy.toFixed(3);
return msg;

flow.set("energy_consumed", 0);
flow.set("ac_last_change", Date.now());
return null;

